import Chance from 'chance'

const schema = chance => ({
  id: chance.guid(),
  avatar: chance.avatar({ protocol: 'https', fileExtension: 'jpg' }),
  memberSince: chance.year({ min: 1980, max: 2017 }),
  maritalStatus: chance.weighted(['Single', 'Engaged', 'Married', 'Divorced'], [1, 2, 3, 4]),
  name: chance.name(),
  age: chance.age(),
  phone: chance.phone(),
  email: chance.email(),
  address: [
    {
      name: 'Home',
      value: chance.address()
    },
    {
      name: 'Work',
      value: chance.address()
    }
  ]
})

List.map(i => schema(new Chance()))

